msg.needlecolor = "#001100"; //цвет стрелки
// окрашивание зон
gzone[0] = {start:0,end:10,color:"#ff5d4ebf"};
gzone[1] = {start:10,end:20,color:"#ffb52d8c"};
gzone[2] = {start:20,end:25,color:"#91ff4e85"};
gzone[3] = {start:25,end:100,color:"#4ec3ff85"};




// ----------------------------------------------------------------

let gtick = []; let gsubtick = []; let gnum = [];
Gauge = (start,end) => {
        let x1 = 100*Math.tan((90-90/msg.gsubtick_count*start)*Math.PI/180);
        if ((90/msg.gsubtick_count*start) <= 45) {x1=100;}
        let x2 = 100*Math.tan((90-90/msg.gsubtick_count*end)*Math.PI/180);
        if ((90/msg.gsubtick_count*end) <= 45) {x2=100;}
    if (msg.gbody == "left") {
        x1 = 100-100*Math.tan((90-90/msg.gsubtick_count*start)*Math.PI/180);
        if ((90/msg.gsubtick_count*start) <= 45) {x1=0;}
        x2 = 100-100*Math.tan((90-90/msg.gsubtick_count*end)*Math.PI/180);
        if ((90/msg.gsubtick_count*end) <= 45) {x2=0;}
    }

    let y1 = 100-100*Math.tan(90/msg.gsubtick_count*start*Math.PI/180);
    if (y1 < 0) {y1=0;}
    let y2 = 100-100*Math.tan((90/msg.gsubtick_count*end)*Math.PI/180);
    if (y2 < 0) {y2=0;}

    if (msg.gbody == "left") {
    topic = "clip-path: polygon("+x1+"% "+y1+"%, 100% 100%, "+x2+"% "+y2+"%, 0% 0%)";
    } else {
    topic = "clip-path: polygon("+x1+"% "+y1+"%, 100% 0%, "+x2+"% "+y2+"%, 0% 100%)";
    }
    return topic;
}
msg.bord = Math.floor(7/4*msg.containersize);
if (msg.gbody == "left") {
    msg.bt = msg.bord;
    msg.br = 0;
    msg.bl = msg.bord;
    msg.ll = 8;
    msg.br1 = 100;
    msg.br2 = 0;
} else {
    msg.bt = msg.bord;
    msg.br = msg.bord;
    msg.bl = 0;
    msg.ll = 10;
    msg.br1 = 0;
    msg.br2 = 100;
}
if (msg.gbody == "right" || msg.gbody == "left") {
    for (let i = 0; i < gzone.length; i++) {
        gzone[i].zone = Gauge(gzone[i].start,gzone[i].end);
    }
    msg.gbody2 = "quarter-top-" + msg.gbody
    msg.gval = ""
    msg.gvalring = "g-val-ring";
    msg.gvalplate = "g-val-plate";
    msg.gaugevalue = 100;
    msg.gndistance = 28;
    msg.grivet = "";
    msg.gzone = gzone;

}else{
    di =  gzone.length;
    dd =  gzone.length;
    for (let i = 0; i < dd; i++) {
        gzonep = 134.7+gzone[i].start*(270/msg.gsubtick_count);
        gzone[i].zone = gzonep;
        zonem = 90-(gzone[i].end-gzone[i].start)*(270/msg.gsubtick_count);
        if (zonem < -45) {
            gzone[i].zone2 = -45;
            gzone[di] = {color:gzone[i].color, zone:gzonep+135,zone2:zonem+135};
            di++;
        } else {
            gzone[i].zone2 = 90-(gzone[i].end-gzone[i].start)*(270/msg.gsubtick_count);
        }
    }
    msg.gbody2 = "";
    msg.gvalring = "";
    msg.gvalplate = "";
    msg.gval = "top: 65%; right:50%-calc(calc(var(--container-size) * 7%) / calc(var(--container-size)/4)); border-radius:0%";
    msg.gaugevalue = 0;
    msg.gndistance = 13;
    msg.grivet = "g-rivet";
    msg.gzone2 = gzone;

}

for (let i = 1; i <= msg.gsubtick_count+1; i++) {
gsubtick[i-1] = {ga:i};
}
msg.gsubtick = gsubtick;
for (let i = 1; i <= msg.gtick_count+1; i++) {
gtick[i-1] = {ga:i};
}
msg.gtick = gtick;
for (let i = 1; i <= msg.gnum_count+1; i++) {
    if (msg.gbody == "right") {ii=i;} else {ii=msg.gnum_count+2-i}
    if (i==1) {
        gnum[i-1] = {ga:ii,gs:msg.gnum_count,value:msg.max};
    } else {
        gnum[i-1] = {ga:ii,gs:msg.gnum_count,value:msg.max-(msg.max-msg.min)/msg.gnum_count*(i-1)};
    }
}
msg.gnum = gnum;
msg.payload = value * 1;
if (msg.payload > msg.max+msg.max/msg.gsubtick_count*3){
    msg.value = msg.max+msg.max/msg.gsubtick_count*3;
} else if (msg.payload < msg.min-msg.max/msg.gsubtick_count*3) {
    msg.value = msg.min-msg.max/msg.gsubtick_count*3;
} else {
    msg.value = msg.payload;
}

if (msg.gbody == "right") {
msg.v = 100 - (((msg.value - msg.min) / (msg.max - msg.min)) * 100);
} else if (msg.gbody == "left") {
msg.v = 100 + (((msg.value - msg.min) / (msg.max - msg.min)) * 100);
} else {
msg.v = ((msg.value - msg.min) / (msg.max - msg.min)) * 100;
}
return msg;

